var buttons=document.querySelectorAll("button"),inputField=document.querySelector("input"),buildEvents=function(array){buttons.forEach(function(button){button.addEventListener("click",function(){var inputFieldContents=inputField.value;if("C"===this.innerHTML)inputField.value="";else if("Del"===this.innerHTML)inputField.value=inputFieldContents.slice(0,-1);else if("="===this.innerHTML){var results=eval(inputFieldContents);inputField.value=results}else"รท"===this.innerHTML?inputField.value=inputFieldContents+"/":"X"===this.innerHTML?inputField.value=inputFieldContents+"*":inputField.value=inputFieldContents+this.innerHTML})})};buildEvents(buttons);